topics = {}

for item in _items:
    j = item.get("json") if isinstance(item, dict) else None
    if not isinstance(j, dict):
        j = item if isinstance(item, dict) else {}

    topic = (j.get("topic_name") or "General").strip()

    raw_date = j.get("date")
    ts = "UNKNOWN_TIME"

    if isinstance(raw_date, str) and "T" in raw_date:
        try:
            date_part, time_part = raw_date.replace("Z", "").split("T", 1)
            hh = int(time_part[0:2]) + 4  # UTC -> Tbilisi
            mm = time_part[3:5]

            # rollover
            if hh >= 24:
                hh -= 24
                y = int(date_part[0:4])
                m = int(date_part[5:7])
                d = int(date_part[8:10])

                leap = (y % 4 == 0 and (y % 100 != 0 or y % 400 == 0))
                dim = [31, 29 if leap else 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31][m - 1]

                d += 1
                if d > dim:
                    d = 1
                    m += 1
                    if m > 12:
                        m = 1
                        y += 1
                date_part = f"{y:04d}-{m:02d}-{d:02d}"

            ts = f"{date_part} {hh:02d}:{mm}"
        except Exception:
            ts = "UNKNOWN_TIME"

    first = (j.get("from_first_name") or "").strip()
    last = (j.get("from_last_name") or "").strip()
    username = (j.get("from_username") or "").strip()

    name = " ".join(x for x in [first, last] if x).strip()
    if not name and username:
        name = f"@{username}"
    elif username:
        name = f"{name} (@{username})" if name else f"@{username}"

    text = (j.get("text") or "").strip()
    if not text:
        continue

    line = f"[{ts}] {name}: {text}"
    topics.setdefault(topic, []).append(line)

out = []
for topic, lines in topics.items():
    out.append({
        "json": {
            "topic_name": topic,
            "dialog": "\n".join(lines),
            "length": len(lines)
        }
    })

return out

