={{ (function() {
  try {
    const input = $("Aggregate").item.json.data;
    if (!input || !Array.isArray(input)) return "‚ö†Ô∏è –î–∞–Ω–Ω—ã–µ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã";

    const treatment = input.filter(i => i['–¢–∏–ø'] === '–ª–µ—á–µ–Ω–∏–µ');
    const check = input.filter(i => i['–¢–∏–ø'] === '–ø—Ä–æ–≤–µ—Ä–∫–∞');

    let message = "üìã **–ï–ñ–ï–î–ù–ï–í–ù–´–ô –û–¢–ß–ï–¢ –ü–û –ú–ï–î–£–•–û–î–£**\n\n";

    if (treatment.length > 0) {
      message += "üíä **–¢–ï–ö–£–©–ï–ï –õ–ï–ß–ï–ù–ò–ï:**\n";
      message += treatment.map(item => {
        const cat = item['cat_number_txt'] || '---';
        const room = Array.isArray(item['–ö–æ–º–Ω–∞—Ç–∞']) ? item['–ö–æ–º–Ω–∞—Ç–∞'][0] : (item['–ö–æ–º–Ω–∞—Ç–∞'] || '---');
        const med = Array.isArray(item['Name (from –õ–µ–∫–∞—Ä—Å—Ç–≤–æ+—Å—Ä–æ–∫ –≥–æ–¥–Ω–æ—Å—Ç–∏)']) ? item['Name (from –õ–µ–∫–∞—Ä—Å—Ç–≤–æ+—Å—Ä–æ–∫ –≥–æ–¥–Ω–æ—Å—Ç–∏)'][0] : (item['med_txt'] || '–º–∞–Ω–∏–ø—É–ª—è—Ü–∏—è');
        const loc = Array.isArray(item['Med_location']) ? item['Med_location'][0] : '---';

        // .trim() —É–±–∏—Ä–∞–µ—Ç –ª–∏—à–Ω–∏–µ –ø–µ—Ä–µ–Ω–æ—Å—ã –∏–∑ —Å–∞–º–æ–π –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
        const log = (item['–ö–æ–º–º–µ–Ω—Ç (–æ–±—â–∏–π)'] || '-').toString().trim();
        const dateEnd = item['–î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è –ø—Ä–∏–µ–º–∞'] || '---';

        return `\nüê± **–ö–æ—à–∫–∞:** ${cat} (–∫–æ–º–Ω. ${room})\n` +
               `üíä **–õ–µ–∫–∞—Ä—Å—Ç–≤–æ:** ${med}\n` +
               `üì¶ **–†–∞—Å–ø–æ–ª–æ–∂–µ–Ω–∏–µ:** ${loc}\n` +
               `üí¨ **–ö–æ–º–º–µ–Ω—Ç:** ${log}\n` +
               `‚åõ **–û–∫–æ–Ω—á–∞–Ω–∏–µ:** ${dateEnd}\n` +
               `‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ`;
      }).join('\n'); // –°–æ–µ–¥–∏–Ω—è–µ–º –∑–∞–ø–∏—Å–∏ –±–µ–∑ –ª–∏—à–Ω–∏—Ö –ø—É—Å—Ç—ã—Ö —Å—Ç—Ä–æ–∫
    }

    if (check.length > 0) {
      if (treatment.length > 0) message += "\n\n"; // –û—Ç—Å—Ç—É–ø —Ç–æ–ª—å–∫–æ –º–µ–∂–¥—É –±–ª–æ–∫–∞–º–∏
      const catNumbers = check.map(item => {
        const num = item['cat_number_txt'];
        return Array.isArray(num) ? num[0] : num;
      }).filter(n => n).join(', ');

      if (catNumbers) message += `üßµ **–ü–†–û–í–ï–†–ö–ê –®–í–û–í (–Ω–æ–º–µ—Ä–∞):**\n${catNumbers}`;
    }

    return message;
  } catch (e) {
    return "‚ùå –û—à–∏–±–∫–∞: " + e.message;
  }
})() }}

